import React, { useState } from 'react';
import './Styles.css'
import Header from './Header'
import Footer from './Footer'
import axios from 'axios';
import { Redirect } from 'react-router';

function AddConnection() {

    const [connectionId, setConnectionId] = useState('');
    const [createdDate, setCreatedDate] = useState('');
    const [status, setStatus] = useState('');
    const [customerId, setCustomerId] = useState('');
    const [tariffId, setTariffId] = useState('');
    const [submitted, setSubmitted] = useState(false);

    const handleSubmit = (event) => {

        let payload = {
            connectionId: connectionId,
            createdDate: createdDate,
            status: status,
            customerId: customerId,
            tariffId: tariffId
        }
        console.log(payload);

        axios.post("http://localhost:8080/connection/save", payload).then(resp => setSubmitted(true));
        console.log("Connection saved :" + payload.connectionId);

        event.preventDefault();
    }
    return (
        <><><Header />
            <div className="container-fluid">
                {
                    submitted && <Redirect to="/allConnection" />}
                <h2>Add Connection</h2>
                <div className="form-group">
                    <label htmlFor="customerId">Customer Id</label>
                    <input type="text" name="desc" value={customerId} onChange={(e) => setCustomerId(e.target.value)} className="form-control" placeholder="Enter Customer Id " id="customerId" required />
                </div>
                <div className="form-group">
                    <label htmlFor="tariffId">Tariff Id </label>
                    <input type="text" name="desc" value={tariffId} onChange={(e) => setTariffId(e.target.value)} className="form-control" placeholder="Enter Tariff Id " id="tariffId" required />
                </div>

                <div>
                    <button onClick={handleSubmit} className="btn btn-primary">Add Connection</button>
                </div>

            </div>
        </><Footer /></>
    )
}
export default AddConnection;
