import Header from './Header';
import Footer from './Footer';

import React from 'react';
import { connect } from 'react-redux';

import { fetchConsumptionById } from '../store/actions/ConsumptionAction';

class FetchByConsumptionId extends React.Component {

    componentDidMount() {
        this.props.fetchById(this.props.match.params.id);
    }

    render() {
        return (
            <div>
                <Header/>
                <div className="table-responsive">
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                                <th>Consumption Id</th>
                                <th>Current Reading</th>
                                <th>Current Date</th>
                                <th>Previous Reading</th>
                                <th>Previous Date</th>
                                <th>units Consumed</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.props.consumption !== undefined ?
                                    <tr>
                                        <td>{this.props.consumption.consumptionId}</td>
                                        <td>{this.props.consumption.currentReading}</td>
                                        <td>{this.props.consumption.currentdate}</td>
                                        <td>{this.props.consumption.previousReading}</td>
                                        <td>{this.props.consumption.previousdate}</td>
                                        <td>{this.props.consumption.unitsConsumed}</td>
                                    </tr>
                                : <p> Loading...</p>
                            }
                        </tbody>
                    </table>
                </div>
                <Footer/>
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        consumption : state.consumptionReducer.consumption,        
    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchById : (consumptionId) => dispatch(fetchConsumptionById(consumptionId))
       
    }
}


export default connect(mapStateToProps,mapDispatchToProps)(FetchByConsumptionId);
