const initialState ={
    consumptions : [],
    consumption : undefined,
    result : undefined
}

export default function consumptionReducer(state=initialState, action){

    if(action.type === 'FETCHALLSUCCESS'){

        return{
            ...state,
            consumptions : action.payload
        }
    }

    if (action.type === 'FETCHBYIDSUCCESS') {

        return {
            ...state,
            consumption : action.payload
        }
    }
    
    if (action.type === 'SAVESUCCESS'){
        return{
            ...state,
            result : action.payload
        }
    }
    return state;
}
