import React, { useState } from 'react';
import axios from 'axios';
import Header from './Header';
import Footer from './Footer';
import './Style.css';
import { Redirect } from 'react-router';
function SaveConsumption() {
    
    const [connectionConnectionId, setConnectionConnectionId] = useState('');
    const [currentdate, setCurrentdate] = useState('');
    const [currentReading, setCurrentReading] = useState('');
    const [previousdate, setPreviousdate] = useState('');
    const [previousReading, setPreviousReading] = useState('');
    const [submitted, setSubmitted] = useState(false);
    const handleSubmit = (event) => {
        let payload = {
            connectionConnectionId: connectionConnectionId,
            currentReading: currentReading,
            currentdate: currentdate,
            previousReading: previousReading,
            previousdate: previousdate
        }
        console.log(payload);
        axios.post("http://localhost:8080/consumption/save/"+connectionConnectionId, payload).then(resp =>setSubmitted(true));
        console.log("consumption saved :" + payload.connectionConnectionId);
        event.preventDefault();
    }
    return (
        <div>
        <Header/>
        <div id="fetchvalue">
        <div className="container-fluid">
            {submitted && <Redirect to = "/allConsumption"/>}
        <h2>Add Consumption</h2>
        <p></p>
        
        <div className="form-group">
            <label htmlFor="connectionId">Connection Id</label>
            <input type="number" name="connectionId" value={connectionConnectionId} onChange={(e)=> setConnectionConnectionId(e.target.value)} className="form-control" placeholder="Enter connection Id" id="connectionConnectionId" required/>
        </div>
        <div className="form-group">
            <label htmlFor="currentReading">Current Reading</label>
            <input type="number" name="currentReading" value={currentReading} onChange={(e)=>setCurrentReading(e.target.value)} className="form-control" placeholder="Enter current reading" id="currentReading" required/>
        </div>
        <div className="form-group">
            <label for="currentdate">Current Date</label>
            <input type="date" name="currentdate" value={currentdate} onChange={(e)=>setCurrentdate(e.target.value)} className="form-control" placeholder="Enter current date" id="currentdate" required/>
        </div>
        <div className="form-group">
            <label htmlFor="previousReading">Previous Reading</label>
            <input type="number" name="previousReading" value={previousReading} onChange={(e)=>setPreviousReading(e.target.value)} className="form-control" placeholder="Enter previous reading" id="previousReading" required/>
        </div>
        <div className="form-group">
            <label htmlFor="previousdate">Previous Date</label>
            <input type="date" name="previousdate" value={previousdate} onChange={(e)=>setPreviousdate(e.target.value)} className="form-control" placeholder="Enter previous date" id="previousdate" required/>
        </div>
        <div>
            <button onClick={handleSubmit} className="button">Add</button>
        </div>
    </div>
    </div>
    <Footer/>
    </div>
    )
}
export default SaveConsumption;
