import axios from 'axios';

export function fetchAllConsumptions(){
    return (dispatch) => {

        return axios.get('http://localhost:8080/consumption/all')
        .then(resp => dispatch(fetchAllConsumptionsSuccess(resp.data)));
    }
}

export function fetchAllConsumptionsSuccess(data){
    return{
        type: "FETCHALLSUCCESS",
        payload : data
    }
}

export function fetchConsumptionById(consumptionId) {

    return (dispatch) => {

        return axios.get('http://localhost:8080/consumption/get/'+consumptionId)
            .then(resp => {
                dispatch(fetchConsumptionByIdSuccess(resp.data))
            })
            .catch(error => {
                throw (error);
            });
    }
}

export function fetchConsumptionByIdSuccess(data) {
    return {
        type: "FETCHBYIDSUCCESS",
        payload : data
    }
}

export function saveConsumption(consumption,connectionId){
    return (dispatch) => {

        return axios.post('http://localhost:8080/consumption/save/'+connectionId,consumption)
        .then(resp => {
            dispatch(saveConsumptionSuccess(resp.data))
        })
        .catch(error => {
            throw (error);
        });
    }
}

export function saveConsumptionSuccess(data){
    return{
        type: "SAVESUCCESS",
        payload : data
    }
}
