import React, { useState, useEffect } from 'react'; 
import {Link} from 'react-router-dom';
import axios from 'axios'; 
import Header from './Landing/Header';
import Footer from './Landing/Footer';
import Table from 'react-bootstrap/Table'

function FetchAllConsumptions() {
    
        const [consumptions, setConsumptions] = useState([]); 
        useEffect(() => { 
    
            axios.get('http://localhost:8080/consumption/all') 
    
            .then(resp => setConsumptions(resp.data)); 
    
        },[]); 
    
        return (
            
            <div>
                <Header/>
                <div id="fetch">
                <h1></h1>
                <div className="table-responsive" >
                    <Table responsive="sm">
                        <thead>
                            <tr>
                                <th>Consumption ID</th>
                                <th>Current Reading</th>
                                <th>Current Date</th>
                                <th>Previous Reading</th>
                                <th>Previous Date</th>
                                <th>Units Consumed</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                consumptions.length > 0 ? 
                                consumptions.map(consumption => 
                                    <tr>
                                        <td>{consumption.consumptionId}</td>
                                        <td>{consumption.currentReading}</td>
                                        <td>{consumption.currentdate}</td>
                                        <td>{consumption.previousReading}</td>
                                        <td>{consumption.previousdate}</td>
                                        <td>{consumption.unitsConsumed}</td>
                                        <td><Link to={"/get/"+consumption.consumptionId}>View </Link></td>
                                        <td><Link to={"/update/"+consumption.consumptionId}>Update</Link></td>
                                        <td><Link to={"/delete/"+consumption.consumptionId} style={{color:"red"}}>Delete</Link></td>
                                    </tr>
                                )
                                : <p>Loading...</p> 
                            }
                        </tbody>
                    </Table>
                </div>
                <div>
                    <Link to={"/save/"}><button className="btn btn-primary">Add New Consumption</button></Link>
                </div>
                </div>
                <Footer/>
            
            </div>
        )
    }

export default FetchAllConsumptions;
